import React, { useState, useRef, useEffect } from "react";
import * as d3 from "d3";
import PersonEditForm from "./PersonEditForm";

/** CONFIGURABLE CONSTANTS **/
const RING_WIDTH = 60;         // radial width for each generation ring
const ARC_PADDING = 0;         // gap in degrees between arc segments
const CENTER_RADIUS = 30;      // radius for the center circle
const DEFAULT_FONT_SIZE = 8;   // base font size (px)
const LINE_SPACING = 10;       // radial offset between lines in multi-line wrap

export default function GenealogyChart({
  people,
  maxGenerations,
  centerPersonId,
  onUpdatePeople
}) {
  const [selectedPersonId, setSelectedPersonId] = useState(null);
  const svgRef = useRef(null);

  useEffect(() => {
    if (!people || people.length === 0) return;
    drawChart();
  }, [people, maxGenerations]);

  // ---- TEXT UTILS ----

  /**
   * Approximate pixel width of the given string at the given font size.
   * Naive approach: average char width ~ 0.6 * fontSize.
   */
  function approximateTextWidth(str, fontSize = DEFAULT_FONT_SIZE) {
    const avgCharWidth = fontSize * 0.6;
    return str.length * avgCharWidth;
  }

  /**
   * Naive multi-line word-wrap:
   * - Splits text by spaces
   * - Accumulates words until they'd exceed arcLength
   * - Returns array of lines
   */
  function wrapTextToWidth(str, arcLength, fontSize = DEFAULT_FONT_SIZE) {
    const words = str.split(" ");
    const lines = [];
    let currentLineWords = [];

    for (let i = 0; i < words.length; i++) {
      const testLine = [...currentLineWords, words[i]].join(" ");
      const testWidth = approximateTextWidth(testLine, fontSize);

      if (testWidth <= arcLength) {
        currentLineWords.push(words[i]);
      } else {
        lines.push(currentLineWords.join(" "));
        currentLineWords = [words[i]];
      }
    }
    if (currentLineWords.length > 0) {
      lines.push(currentLineWords.join(" "));
    }
    return lines;
  }

  function drawChart() {
    const width = 800;
    const height = 800;
    const centerX = width / 2;
    const centerY = height / 2;

    // Clear any existing SVG
    d3.select(svgRef.current).selectAll("*").remove();

    // Base SVG
    const svg = d3
      .select(svgRef.current)
      .attr("width", width)
      .attr("height", height);

    // Map of id -> person
    const peopleMap = new Map(people.map((p) => [p.id, p]));

    // BFS arrays: ancestors[i] has 2^i slots
    const ancestors = [];
    for (let i = 0; i < maxGenerations; i++) {
      ancestors[i] = new Array(2 ** i).fill(null);
    }
    // Gen 0 => center
    ancestors[0][0] = centerPersonId;

    // Fill subsequent generations
    for (let i = 0; i < maxGenerations - 1; i++) {
      const size = 2 ** i;
      for (let j = 0; j < size; j++) {
        const pid = ancestors[i][j];
        if (!pid) continue;
        const person = peopleMap.get(pid);
        if (!person) continue;

        // father => 2*j, mother => 2*j+1
        if (person.parents && person.parents.length > 0) {
          ancestors[i + 1][2 * j] = person.parents[0] || null;
        }
        if (person.parents && person.parents.length > 1) {
          ancestors[i + 1][2 * j + 1] = person.parents[1] || null;
        }
      }
    }

    // 1) Center circle
    const centerPerson = peopleMap.get(centerPersonId);
    svg
      .append("circle")
      .attr("cx", centerX)
      .attr("cy", centerY)
      .attr("r", CENTER_RADIUS)
      .attr("fill", "#F6C")
      .attr("stroke", "#333")
      .on("click", () => {
        setSelectedPersonId(centerPersonId);
      });

    svg
      .append("text")
      .attr("x", centerX)
      .attr("y", centerY + 4)
      .attr("text-anchor", "middle")
      .style("font-size", `${DEFAULT_FONT_SIZE}px`)
      .text(
        centerPerson
          ? centerPerson.firstName + " " + centerPerson.lastName
          : "Unknown"
      );

    // 2) Concentric arcs for each generation
    for (let i = 1; i < maxGenerations; i++) {
      const genArray = ancestors[i];
      const segmentCount = 2 ** i;
      // each arc angle minus optional spacing
      const arcAngle = (2 * Math.PI) / segmentCount - (ARC_PADDING * Math.PI) / 180;

      // ring boundaries
      const innerRadius = (i - 1) * RING_WIDTH + CENTER_RADIUS;
      const outerRadius = i * RING_WIDTH + CENTER_RADIUS;

      for (let k = 0; k < segmentCount; k++) {
        const personId = genArray[k];
        const startAngle = k * ((2 * Math.PI) / segmentCount);
        const endAngle = startAngle + arcAngle;

        // Arc generator for ring slice
        const arcGenerator = d3
          .arc()
          .innerRadius(innerRadius)
          .outerRadius(outerRadius)
          .startAngle(startAngle)
          .endAngle(endAngle);

        if (!personId) {
          // blank arc
          svg
            .append("path")
            .attr("transform", `translate(${centerX},${centerY})`)
            .attr("d", arcGenerator)
            .attr("fill", "#eee")
            .attr("stroke", "#ccc");
          continue;
        }

        // Draw the arc
        svg
          .append("path")
          .attr("transform", `translate(${centerX},${centerY})`)
          .attr("d", arcGenerator)
          .attr("fill", "#ddd")
          .attr("stroke", "#999")
          .on("click", () => {
            setSelectedPersonId(personId);
          });

        // Build label
        let label = "Unknown";
        const person = peopleMap.get(personId);
        if (person) {
          label =
            person.firstName +
            " " +
            person.lastName +
            " (" +
            person.birthDate +
            " - " +
            person.deathDate +
            ")";
        }

        // measure arc length at midRadius
        const midRadius = (innerRadius + outerRadius) / 2;
        const angleDiff = Math.abs(endAngle - startAngle);
        const arcLength = angleDiff * midRadius;

        // Wrap text if needed
        const lines = wrapTextToWidth(label, arcLength, DEFAULT_FONT_SIZE);

        // We want line 0 to be the OUTER line, so subsequent lines appear closer to the center
        const numLines = lines.length;
        // totalHeight = (numLines - 1) * LINE_SPACING
        const totalHeight = (numLines - 1) * LINE_SPACING;
        // outermostRadius = midRadius + totalHeight/2 => line 0 is largest radius
        const outermostRadius = midRadius + totalHeight / 2;

        // For each line, define an arc
        // line 0 => outermost, line 1 => inside it, etc.
        lines.forEach((lineText, idx) => {
          const lineRadius = outermostRadius - idx * LINE_SPACING;

          // Arc for this line
          const lineArcGen = d3
            .arc()
            .innerRadius(lineRadius)
            .outerRadius(lineRadius)
            .startAngle(startAngle)
            .endAngle(endAngle);

          // measure THIS line's length
          const lineArcLength = angleDiff * lineRadius;

          // Create path in <defs>
          const textPathId = `textPath-${i}-${k}-line${idx}`;
          svg
            .append("defs")
            .append("path")
            .attr("id", textPathId)
            .attr("transform", `translate(${centerX},${centerY})`)
            .attr("d", lineArcGen());

          // Place text in the center horizontally via pixel offset
          svg
            .append("text")
            .style("font-size", `${DEFAULT_FONT_SIZE}px`)
            .append("textPath")
            .attr("xlink:href", "#" + textPathId)
            .attr("startOffset", (lineArcLength / 2) + "px")
            .style("text-anchor", "middle")
            .text(lineText);
        });
      }
    }
  }

  // Editing form logic
  const handleEditSave = (updatedPerson) => {
    onUpdatePeople((prev) =>
      prev.map((p) => (p.id === updatedPerson.id ? updatedPerson : p))
    );
    setSelectedPersonId(null);
  };

  const handleEditCancel = () => {
    setSelectedPersonId(null);
  };

  const selectedPerson = people.find((p) => p.id === selectedPersonId);

  return (
    <div style={{ position: "relative" }}>
      <svg ref={svgRef}></svg>
      {selectedPerson && (
        <PersonEditForm
          person={selectedPerson}
          onSave={handleEditSave}
          onCancel={handleEditCancel}
        />
      )}
    </div>
  );
}
