import React, { useState, useRef, useEffect } from "react";
import * as d3 from "d3";
import PersonEditForm from "./PersonEditForm";

// Constants
const RING_WIDTH = 30;   // radial width per generation ring
const ARC_PADDING = 0;    // gap in degrees between arc segments
const CENTER_RADIUS = 30; // radius for the center circle

export default function GenealogyChart({
  people,
  maxGenerations,
  centerPersonId,
  onUpdatePeople
}) {
  const [selectedPersonId, setSelectedPersonId] = useState(null);
  const svgRef = useRef(null);

  useEffect(() => {
    if (!people || people.length === 0) return;
    drawChart();
  }, [people, maxGenerations]);

  function drawChart() {
    const width = 800;
    const height = 800;
    const centerX = width / 2;
    const centerY = height / 2;

    // Clear existing content
    d3.select(svgRef.current).selectAll("*").remove();

    // Create the base SVG
    const svg = d3.select(svgRef.current)
      .attr("width", width)
      .attr("height", height);

    // Map of id -> person
    const peopleMap = new Map(people.map((p) => [p.id, p]));

    // Build BFS arrays of ancestors up to maxGenerations
    // ancestors[i] = array of length 2^i
    const ancestors = [];
    for (let i = 0; i < maxGenerations; i++) {
      ancestors[i] = new Array(2 ** i).fill(null);
    }
    // Generation 0: center person
    ancestors[0][0] = centerPersonId;

    // Populate further generations
    for (let i = 0; i < maxGenerations - 1; i++) {
      const size = 2 ** i;
      for (let j = 0; j < size; j++) {
        const personId = ancestors[i][j];
        if (!personId) continue;
        const person = peopleMap.get(personId);
        if (!person) continue;

        // father => 2*j, mother => 2*j+1
        if (person.parents && person.parents.length > 0) {
          ancestors[i + 1][2 * j] = person.parents[0] || null;
        }
        if (person.parents && person.parents.length > 1) {
          ancestors[i + 1][2 * j + 1] = person.parents[1] || null;
        }
      }
    }

    // 1) Draw the center circle
    const centerPerson = peopleMap.get(centerPersonId);
    svg.append("circle")
      .attr("cx", centerX)
      .attr("cy", centerY)
      .attr("r", CENTER_RADIUS)
      .attr("fill", "#F6C")
      .attr("stroke", "#333")
      .on("click", () => {
        setSelectedPersonId(centerPersonId);
      });

    // Label the center circle
    svg.append("text")
      .attr("x", centerX)
      .attr("y", centerY + 4)
      .attr("text-anchor", "middle")
      .style("font-size", "14px")
      .text(
        centerPerson
          ? `${centerPerson.firstName} ${centerPerson.lastName}`
          : "Unknown"
      );

    // 2) Draw concentric rings for generations 1..maxGenerations-1
    for (let i = 1; i < maxGenerations; i++) {
      const genArray = ancestors[i];
      const segmentCount = 2 ** i;

      // Each arc angle minus optional spacing
      const arcAngle = (2 * Math.PI) / segmentCount - (ARC_PADDING * Math.PI) / 180;

      // Ring boundaries
      const innerRadius = (i - 1) * RING_WIDTH + CENTER_RADIUS + 10;
      const outerRadius = i * RING_WIDTH + CENTER_RADIUS;

      for (let k = 0; k < segmentCount; k++) {
        const personId = genArray[k];
        const startAngle = k * ((2 * Math.PI) / segmentCount);
        const endAngle = startAngle + arcAngle;

        // Arc generator for the ring slice
        const arcGenerator = d3.arc()
          .innerRadius(innerRadius)
          .outerRadius(outerRadius)
          .startAngle(startAngle)
          .endAngle(endAngle);

        // Arc for the label (at midpoint radius)
        const midRadius = (innerRadius + outerRadius) / 2;
        const labelArcGenerator = d3.arc()
          .innerRadius(midRadius)
          .outerRadius(midRadius)
          .startAngle(startAngle)
          .endAngle(endAngle);

        // If no person, optionally draw a blank arc
        if (!personId) {
          svg.append("path")
            .attr("transform", `translate(${centerX},${centerY})`)
            .attr("d", arcGenerator)
            .attr("fill", "#eee")
            .attr("stroke", "#ccc");
          continue;
        }

        // Draw the interactive arc
        svg.append("path")
          .attr("transform", `translate(${centerX},${centerY})`)
          .attr("d", arcGenerator)
          .attr("fill", "#ddd")
          .attr("stroke", "#999")
          .on("click", () => {
            setSelectedPersonId(personId);
          });

        // Create a path for the text
        const textPathId = `textPath-${i}-${k}`;
        svg.append("defs")
          .append("path")
          .attr("id", textPathId)
          .attr("transform", `translate(${centerX},${centerY})`)
          .attr("d", labelArcGenerator());

        // Build the text label
        const person = peopleMap.get(personId);
        let label = "Unknown";
        if (person) {
          label = `${person.firstName} ${person.lastName} (${person.birthDate} - ${person.deathDate})`;
        }

        // Measure the arc length (approx: angle * radius)
        // If (endAngle - startAngle) is negative, use abs
        const angleDiff = Math.abs(endAngle - startAngle);
        const arcLength = angleDiff * midRadius;

        // Attach the text in the middle of the path
        svg.append("text")
          .append("textPath")
          .attr("xlink:href", "#" + textPathId)
          // arcLength/2 px offset => physically center in the arc
          .attr("startOffset", (arcLength / 2) + "px")
          .style("text-anchor", "middle")
          .style("font-size", "12px")
          .text(label);
      }
    }
  }

  // Editing form handlers
  const handleEditSave = (updatedPerson) => {
    onUpdatePeople((prev) =>
      prev.map((p) => (p.id === updatedPerson.id ? updatedPerson : p))
    );
    setSelectedPersonId(null);
  };

  const handleEditCancel = () => {
    setSelectedPersonId(null);
  };

  const selectedPerson = people.find((p) => p.id === selectedPersonId);

  return (
    <div style={{ position: "relative" }}>
      <svg ref={svgRef}></svg>
      {selectedPerson && (
        <PersonEditForm
          person={selectedPerson}
          onSave={handleEditSave}
          onCancel={handleEditCancel}
        />
      )}
    </div>
  );
}
